mapper.select:
select

from MTM.CTProperty


        and ID = #{paramCondition.id}


        and CTID = #{paramCondition.ctid}


        and BranchID = #{paramCondition.branchID}


        and Address like concat('%',#{paramCondition.address},'%')


        and AddressEN like concat('%',#{paramCondition.addressEN},'%')


        and CompanyID like concat('%',#{paramCondition.companyID},'%')


        and Contacts like concat('%',#{paramCondition.contacts},'%')


        and ContactsEN like concat('%',#{paramCondition.contactsEN},'%')


        and CreateTime = #{paramCondition.createTime}


        and CreditAmount = #{paramCondition.creditAmount}


        and CustomerUSBID like concat('%',#{paramCondition.customerUSBID},'%')


        and LogisticsTypeID = #{paramCondition.logisticsTypeID}


        and NameFull like concat('%',#{paramCondition.nameFull},'%')


        and NameFullEN like concat('%',#{paramCondition.nameFullEN},'%')


        and NameShort like concat('%',#{paramCondition.nameShort},'%')


        and NameShortEN like concat('%',#{paramCondition.nameShortEN},'%')


        and OracleID like concat('%',#{paramCondition.oracleID},'%')


        and OracleIDSupplier like concat('%',#{paramCondition.oracleIDSupplier},'%')


        and Phone like concat('%',#{paramCondition.phone},'%')


        and PropertiesID = #{paramCondition.propertiesID}


        and Settlement = #{paramCondition.settlement}


        and SupplierUSBID like concat('%',#{paramCondition.supplierUSBID},'%')


        and TERMS = #{paramCondition.terms}


        and TaxNum like concat('%',#{paramCondition.taxNum},'%')


        and NameInvoice like concat('%',#{paramCondition.nameInvoice},'%')


        and IsOracleExternal = #{paramCondition.isOracleExternal}


        and Email like concat('%',#{paramCondition.email},'%')


        and UpdateTime = #{paramCondition.updateTime}


        and CustomerLevelID = #{paramCondition.customerLevelID}


        and SalesManID = #{paramCondition.salesManID}


        and Fax like concat('%',#{paramCondition.fax},'%')


        and Freezen = #{paramCondition.freezen}


        and CHDState = #{paramCondition.cHDState}


        and LastEditorID = #{paramCondition.lastEditorID}


        and LastEditTime = #{paramCondition.lastEditTime}


        and CDHReturnInfo like concat('%',#{paramCondition.cDHReturnInfo},'%')


        and UpdateIndex = #{paramCondition.updateIndex}


        and CSSState = #{paramCondition.cSSState}


        and CSSReturnInfo like concat('%',#{paramCondition.cSSReturnInfo},'%')


        and IsOnlySettlement = #{paramCondition.isOnlySettlement}


        and CustSupCode like concat('%',#{paramCondition.custSupCode},'%')


        and CRMStatus = #{paramCondition.cRMStatus}


        and SRMStatus = #{paramCondition.sRMStatus}


        and CreatorID = #{paramCondition.creatorID}


        and IsNewCustomerCTP = #{paramCondition.isNewCustomerCTP}


        and BusinessLine like concat('%',#{paramCondition.businessLine},'%')


        and IsReimbursement = #{paramCondition.isReimbursement}


        and CustomerStatus = #{paramCondition.customerStatus}


        and ApplyReviseCustomer = #{paramCondition.applyReviseCustomer}


        and CustomerInfo like concat('%',#{paramCondition.customerInfo},'%')


        and ReviseCustomerRemark like concat('%',#{paramCondition.reviseCustomerRemark},'%')


        and RejectRemark like concat('%',#{paramCondition.rejectRemark},'%')


        and IsSendEmail = #{paramCondition.isSendEmail}


        and VendorTerms = #{paramCondition.vendorTerms}


        and AddCustomerInfoRemark like concat('%',#{paramCondition.addCustomerInfoRemark},'%')


        and IsVNCustomer = #{paramCondition.isVNCustomer}


        and OracleID1 like concat('%',#{paramCondition.oracleID1},'%')


        and OracleIDSupplier1 like concat('%',#{paramCondition.oracleIDSupplier1},'%')


        and OracleID2 like concat('%',#{paramCondition.oracleID2},'%')


        and OracleIDSupplier2 like concat('%',#{paramCondition.oracleIDSupplier2},'%')


        and OriginalMessage like concat('%',#{paramCondition.originalMessage},'%')
